DROP TABLE IF EXISTS `b_book`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `b_book` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `tid` bigint unsigned DEFAULT NULL,
  `category_id` bigint unsigned DEFAULT NULL,
  `first_chapter_id` bigint unsigned DEFAULT NULL,
  `type_details` int DEFAULT '1' COMMENT '            - 小说\n            - 诗歌\n            - 散文\n            - 童话\n            - 传记',
  `writer_id` bigint unsigned DEFAULT NULL,
  `name` varchar(64) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `icon` longtext COLLATE utf8mb4_general_ci,
  `hidden_words` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `summary` longtext COLLATE utf8mb4_general_ci,
  `sort` int DEFAULT NULL,
  `scope` int unsigned DEFAULT NULL,
  `status` int NOT NULL DEFAULT '1' COMMENT '1 ok 2 off 3 home show',
  `theme` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '#96b97d,dark,#f6f4f0,dark\n\ntop:light\ntop:dark\nnav:light\nnav:dark',
  `click_num` bigint unsigned NOT NULL DEFAULT '0',
  `publish_time` varchar(16) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `ex1` varchar(64) COLLATE utf8mb4_general_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `tid` (`tid`),
  KEY `category_id` (`category_id`),
  KEY `writer_id` (`writer_id`),
  CONSTRAINT `b_book_ibfk_1` FOREIGN KEY (`tid`) REFERENCES `t_time_history` (`id`) ON DELETE CASCADE,
  CONSTRAINT `b_book_ibfk_2` FOREIGN KEY (`category_id`) REFERENCES `b_book_category` (`id`) ON DELETE CASCADE,
  CONSTRAINT `b_book_ibfk_3` FOREIGN KEY (`writer_id`) REFERENCES `b_writer` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

