--
-- Table structure for table `b_book_category`
--

DROP TABLE IF EXISTS `b_book_category`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `b_book_category` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(64) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `icon` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `color` varchar(64) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `hidden_words` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `type` int DEFAULT NULL COMMENT '1童话与儿童 2魔法与幻想 3战争与历史 4浪漫与爱情 5诗歌与艺术 6动作与冒险 7人物的故事 8动作与冒险 9神秘与侦探 10 科幻与宇宙 11惊悚与犯罪 12经济与理财 13时间的痕迹 14武器库 15问题与答案',
  `sort` int DEFAULT NULL,
  `status` int DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

