  `icon` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `summary` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `ex1` varchar(64) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `words` longtext COLLATE utf8mb4_general_ci,
  `sort` int DEFAULT NULL,
  `status` int DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `tid` (`tid`),
  CONSTRAINT `b_writer_ibfk_1` FOREIGN KEY (`tid`) REFERENCES `t_time_history` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
