INSERT INTO `b_writer` (`id`, `tid`, `name`, `icon`, `summary`, `ex1`, `words`, `sort`, `status`, `created_at`, `updated_at`) VALUES (7,2,'安托万·德·圣埃克絮佩里','','1900年6月29日－1944年7月31日 法国作家、飞行员，生于里昂，以著作《小王子》闻名。1944年披甲对抗纳粹德军，于7月31日执行飞行任务时失踪，后获得“法兰西烈士”称号。其他著名的小说有《夜航》、《手斧少年》、《空军飞行员》等。',NULL,'',0,1,'2022-01-07 12:16:21','2022-01-07 04:17:37'),(8,4,'格林兄弟	','','',NULL,'',0,1,'2022-01-07 12:44:31','2022-01-07 04:44:31'),(9,6,'安徒生','','丹麦作家暨诗人，因为其童话作品而闻名于世，童话中带出含义，哲学家。其笔下著名的童话故事包括《冰雪女王》、《拇指姑娘》、《卖火柴的小女孩》、《丑小鸭》和《国王的新衣》等。安徒生生前获得皇家致敬，被高度赞扬为给予全欧洲的一代孩子带来了欢乐。他的作品被翻译为超过150种语言，成千上万册童话书在全球陆续发行出版。他的童话故事还激发了大量电影、舞台剧、芭蕾舞剧及动画的创作。',NULL,'',0,1,'2022-01-07 13:10:14','2022-01-07 05:10:14'),(10,8,'曹文轩','','1954年出生在江苏省盐城市，2016年获得安徒生奖，该奖素有诺贝尔儿童文学奖的美誉。',NULL,'',0,1,'2022-01-07 13:18:14','2022-01-07 05:18:14'),(11,10,'让-亨利·法布尔','','1823年12月22日－1915年10月11日\n法国博物学家、昆虫学家、科普作家，以《昆虫记》（Souvenirs entomologiques）一书留名后世，该书在法国自然科学史与文学史上都具重要地位，已译成多种不同语言。',NULL,'',0,1,'2022-01-07 13:26:22','2022-01-07 05:27:35'),(12,12,'J·R·R·托尔金','','1892年1月3日－1973年9月2日\n英国作家、诗人、语言学家及大学教授，以创作经典古典奇幻作品《霍比特人》、《魔戒》与《精灵宝钻》而闻名于世。',NULL,'',0,1,'2022-01-07 13:35:26','2022-01-07 05:35:26'),(13,14,'J·K·罗琳','','乔安娜·罗琳\n1965年7月31日－\n笔名J·K·罗琳（英语：J. K. Rowling）英国小说家、电影编剧及制片人，代表作为《哈利波特》系列作品。',NULL,'',0,1,'2022-01-07 13:42:21','2022-01-07 05:42:21'),(14,16,'吴承恩	','','1506年－1582年，字汝忠，号射阳山人或射阳居士，淮安府山阳县（今江苏省淮安市淮安区）人，吴承恩父亲吴国迁由安庆府桐城县高甸至淮安府山阳县。其代表作为《西游记》。',NULL,'',0,1,'2022-01-07 13:48:10','2022-01-07 05:48:10'),(15,18,'简·奥斯汀','','英语：Jane Austen，1775年12月16日－1817年7月18日\n英国小说家。\n她的五部主要作品诠释评论了18世纪末英国地主乡绅的生活。她出版的第二部小说《傲慢与偏见》是她在世时评价最高的作品。她小说中的情节常常反映了女性为追求社会地位和经济保障而把婚姻作为依靠。她的作品批评十八世纪下半叶的感性的小说，而且她的小说是十九世纪向现实主义转换的一部分。',NULL,'',0,1,'2022-01-07 13:53:40','2022-01-07 05:54:51'),(16,20,'玛格丽特·米切尔','','1900年11月8日－1949年8月16日，生于美国佐治亚州亚特兰大，美国文学家，世界文学名著《乱世佳人》的作者。',NULL,'',0,1,'2022-01-07 14:04:10','2022-01-07 06:04:10'),(17,22,'夏洛特·勃朗特','','1816年4月21日－1855年3月31日，19世纪英国女作家、诗人，世界文学著作《简·爱》的作者，勃朗特三姐妹之一。其作品革新了虚构小说的框架，是最早在故事中使用私我意识的作家，也是英国文学史上以女性呼声作为小说主题的第一人。',NULL,'',0,1,'2022-01-07 14:11:16','2022-01-07 06:11:16'),(18,24,'维克多·雨果','','1802年2月26日－1885年5月22日，法国浪漫主义文学的代表人物和19世纪前期积极浪漫主义文学运动的领袖，法国文学史上卓越的作家。雨果几乎经历了19世纪法国的所有重大事变。一生创作了众多诗歌、小说、剧本、各种散文和文艺评论及政论文章。代表作有《钟楼怪人》、《九三年》、和《悲惨世界》等。在法国，雨果主要以诗集纪念，如《静观集》和《历代传说》。他创作了4000多幅画，积极参与许多社会运动，如废除死刑。',NULL,'',0,1,'2022-01-07 14:37:02','2022-01-07 06:37:02'),(19,26,'列夫·托尔斯泰','','1828年9月9日－1910年11月20日，俄国小说家、哲学家、政治思想家，也是非暴力的基督教无政府主义者和教育改革家。他是托尔斯泰家族这个贵族家族中最有影响力的一位。',NULL,'',0,1,'2022-01-07 14:42:22','2022-01-07 06:42:22'),(20,28,'罗贯中','','罗贯中（1320年－1400年），名本，字彦直，另一字贯中，以字行，号湖海散人，元末明初通俗小说家，四大名著之一《三国演义》的编作者。',NULL,'',0,1,'2022-01-07 14:45:57','2022-01-07 06:45:57'),(21,30,'查尔斯·狄更斯','','1812年2月7日－1870年6月9日\n英国19世纪中期（维多利亚时代）作家、评论家。小说作品反映出个人的成长经验与社会的时代脉络，广受当时与后世评论家、学者及读者的认可与喜爱。',NULL,'',0,1,'2022-01-07 14:51:12','2022-01-07 06:51:12'),(22,32,'本杰明·富兰克林','','1706年1月17日－1790年4月17日\n出生于美国麻省波士顿，美国博学家、开国元勋之一。他是杰出的政治家、外交家、科学家、发明家，同时亦是出版商、印刷商、记者、作家、慈善家、共济会的成员。作为科学家，他因电学发现和理论成为美国启蒙时代和物理学史上重要人物。作为发明家，他因避雷针、双目眼镜、富兰克林壁炉等闻名。他创立了许多民间组织，包括费城消防站和宾夕法尼亚大学。',NULL,'',0,1,'2022-01-07 14:55:35','2022-01-07 06:55:35'),(23,35,'马克西姆·高尔基','','1868年3月28日－1936年6月18日，笔名马克西姆·高尔基，社会主义、现实主义文学奠基人，政治活动家。',NULL,'',0,1,'2022-01-07 15:04:20','2022-01-07 07:04:20'),(24,44,'儒勒·凡尔纳','','1828年2月8日－1905年3月24日，或译儒勒·凡尔纳，法国小说家、剧作家、诗人，现代科幻小说的重要开创者之一。他以其大量著作和突出贡献，被誉为“科幻小说之父”。',NULL,'',0,1,'2022-01-07 15:24:31','2022-01-07 07:24:31'),(25,48,'博多·舍费尔','','博多·舍费尔 1960年9月10日出生在德国科隆，16岁时移民美国。代表作《小狗钱钱 》。',NULL,'',0,1,'2022-01-07 15:33:36','2022-01-08 12:42:45'),(26,50,'金庸','','1924年3月10日－2018年10月30日，笔名金庸，籍贯浙江海宁，中国近现代著名的文学家和社会活动家，武侠小说泰斗，1948年起到终老，一直在香港生活。自1950年代起，其以笔名“金庸”创作多部脍炙人口的武侠小说，包括《射雕英雄传》、《神雕侠侣》、《倚天屠龙记》、《天龙八部》、《笑傲江湖》、《鹿鼎记》等。',NULL,'',0,1,'2022-01-07 15:38:50','2022-01-08 12:40:00'),(33,56,'海明威','','1899年7月21日－1961年7月2日\n\n美国、古巴记者和作家，他是20世纪最著名的小说家之一。海明威出生于美国伊利诺伊州芝加哥市郊区的奥克帕克，晚年在爱达荷州凯彻姆的家中自杀身亡。海明威一生中的感情错综复杂，先后结过四次婚，是美国“迷失的一代”作家中的代表人物，作品中对人生、世界、社会都表现出了迷茫和彷徨。','1899/7/21','',0,0,'2022-01-10 11:49:52','2022-01-24 12:51:09');
/*!40000 ALTER TABLE `b_writer` ENABLE KEYS */;
UNLOCK TABLES;

