CREATE TABLE `s_icon` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `content` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `status` int DEFAULT '1',
  `created_at` datetime DEFAULT NULL,
